import axios from "axios";

const API_BASE_URL = process.env.NODE_ENV === "production"
  ? "https://seu-backend.onrender.com"  // ðŸŸ¢ URL do back-end online
  : "http://localhost:8000";             // ðŸ”µ URL local (para desenvolvimento)

const Api = axios.create({
  baseURL: API_BASE_URL
});

export default Api;
